global.schema:
type AccountTransactionsState {
  transactions: [TransactionEntry!]!
  budgets: [Budget!]!
}

type TransactionEntry {
  id: ID!
  fromAccount: EthereumAddress
  toAccount: EthereumAddress
  amount: Amount_Money!
  datetime: DateTime!
  details: TransactionDetails!
  budget: OID
}

union TransactionDetails = CryptoTransactionDetails | BankTransactionDetails

type CryptoTransactionDetails {
  txHash: String!
  token: Currency!
  blockNumber: Int
}

type BankTransactionDetails {
  currency: Currency!
  transactionId: String
  referenceNumber: String
}

type Budget {
  id: OID!
  name: OLabel
}

operations.schema:
input CreateTransactionInput {
  fromAccount: EthereumAddress
  toAccount: EthereumAddress
  amount: Amount_Money!
  datetime: DateTime!
  details: TransactionDetailsInput!
  budget: OID
}

input TransactionDetailsInput {
  crypto: CryptoTransactionDetailsInput
  bank: BankTransactionDetailsInput
}

input CryptoTransactionDetailsInput {
  txHash: String!
  token: Currency!
  blockNumber: Int
}

input BankTransactionDetailsInput {
  currency: Currency!
  transactionId: String
  referenceNumber: String
}

input Budget {
  id: OID!
  name: OLabel
}

input UpdateTransactionInput {
  fromAccount: EthereumAddress
  toAccount: EthereumAddress
  amount: Amount_Money
  datetime: DateTime
  details: TransactionDetailsInput
  budget: OID
}

input TransactionDetailsInput {
  crypto: CryptoTransactionDetailsInput
  bank: BankTransactionDetailsInput
}

input CryptoTransactionDetailsInput {
  txHash: String
  token: Currency
  blockNumber: Int
}

input BankTransactionDetailsInput {
  currency: Currency
  transactionId: String
  referenceNumber: String
}

input Budget {
  id: OID!
  name: OLabel
}

input UpdateTransactionBudgetInput {
  txId: ID!,
  budgetId: OID!
}
