operations.schema:
input UpdateKycStatusInput {
    id: OID!
    KycAmlStatus: KycAmlStatusTypeInput!
}

global.schema:
type AccountsState {
   accounts: [AccountEntry!]!
}

type AccountEntry {
    id: OID!
    account: String!
    name: String!
    budgetPath: String
    accountTransactionsId: PHID
    chain: [String!]
    type: AccountType
    owners: [String!]
    KycAmlStatus: KycAmlStatusType
}

enum AccountType {
    Protocol
    Auditor
    Operational
    PaymentProcessor
}

enum KycAmlStatusType {
    PASSED
    PENDING
    FAILED
}
