global.schema:
type AccountsState {
    accounts: [ AccountEntry! ]!
}

type AccountEntry {
		id: OID!
		name: OLabel  
		accountTransactionsId: PHID
		chain: String
		account: EthereumAddress
		budgetPath: String
		type: AccountType
		owners: [PHID]
}

enum AccountType {
	Protocol
	Auditor
	Operational
	Payment Processor
}
