global.schema:
type AccountsState {
    accounts: [ AccountEntry! ]!
}

type AccountEntry {
		id: OID!
		name: OLabel  
		account: EthereumAddress
		budgetPath: String
		type: AccountType
}

enum AccountType {
	Protocol
	Auditor
	Operational
	Payment Processor
}

operations.schema:
input CreateAccountInput {
  name: OLabel
  account: EthereumAddress!
  budgetPath: String
  type: AccountType!
}

enum AccountType {
	Protocol
	Auditor
	Operational
	Payment Processor
}

input UpdateAccountInput {
  name: OLabel
  account: EthereumAddress
  budgetPath: String
  type: AccountType
}
